import React, { useState } from "react";
import ExpenseChart from "./components/ExpenseChart";
import ExpenseForm from "./components/ExpenseForm";
import ExpenseList from "./components/ExpenseList";
import "./styles.css";

const MyComponent = () => {
  const [expenses, setExpenses] = useState([]);

  const handleExpenseSubmit = (expense) => {
    // Add the new expense to the expenses list
    setExpenses((prevExpenses) => [...prevExpenses, expense]);
  };

  return (
    <div className="container">
      <h1 className="section-title">>Add Expense</h1>
      <ExpenseForm onExpenseSubmit={handleExpenseSubmit} />
      <h1 className="section-title">>Expense List :D</h1>
      <ExpenseList expenses={expenses} />
      <h1 className="section-title">Expense Chart :3</h1>
      <ExpenseChart expenses={expenses} />
    </div>
  );
};

export default MyComponent;
